mapper.select:
select p.product_id as productId, p.sku as sku, p.create_person as userId, u.realname as createPerson, p.meigong as meigong, um.realname as meigongName, p.kaifa as kaifa, uk.realname as kaifaName, p.shangchuan as shangchuan, us.realname as shangchuanName, p.paipin as paipin, up.realname as paipinName, p.create_time as createTime FROM product p LEFT JOIN user u on u.user_id = p.create_person LEFT JOIN user um on um.user_id = p.meigong LEFT JOIN user uk on uk.user_id = p.kaifa LEFT JOIN user us on us.user_id = p.shangchuan LEFT JOIN user up on up.user_id = p.paipin
p.del_flag = 0 and p.sku like CONCAT('%',#{sku},'%') and p.create_time >= #{startTime} and p.create_time <= #{endTime}
ORDER BY p.create_time desc
limit ${offset}, ${limit}

SELECT count(*)
FROM product p

    p.del_flag = 0
    and p.sku like CONCAT('%',#{sku},'%')
     and p.create_time >= #{startTime} 
     and p.create_time <= #{endTime}

mapper.update:
update product p set
p.sku = #{sku},p.meigong = #{meigong},p.kaifa = #{kaifa},p.paipin = #{paipin},p.shangchuan = #{shangchuan}
p.del_flag = 1
where p.product_id = #{productId}
